hqmfjs.MeasurePeriod = function(patient) {
  return [new hQuery.CodedEntry(
    {
      "start_time": MeasurePeriod.low.asDate().getTime()/1000,
      "end_time": MeasurePeriod.high.asDate().getTime()/1000,
      "codes": {}
    }
  )];
